select * from etlhubconfirmed.customer_dimension;



select * from fullJoin;

--No change records, ignore
select * from fullJoin WHERE LOWER(nk_hash) = LOWER(existing_existing_nk_hash) and LOWER(column_hash) = LOWER(existing_rec_checksum);

--INSERTS OR NEW ROWS
select COALESCE(existing_MAX_KEY,0) + ROW_NUMBER () OVER (ORDER BY 1) AS CUSTOMER_KEY1, A.* from fullJoin A WHERE existing_existing_nk_hash is null;

--Changed records or update old record and insert new with incremented version
select * from fullJoin WHERE WHERE LOWER(nk_hash) = LOWER(existing_existing_nk_hash) and LOWER(column_hash) <> LOWER(existing_rec_checksum);


--Soft deletes or rows no longer active in source
select * from fullJoin WHERE WHERE nk_hash is null;

--INSERT INTO DBXDH.DHT_CUSTOMER1 
select * from fullJoin where existing_existing_nk_hash is null;

