%%sql
select 'Number of Active Rows' as Title,count(*) from etlhubconfirmed.dht_deal_dim where CURRENT_IND='Y'
;


select 'Duplicate Rows based on surrogate key' as Title,DEAL_KEY,count(*) from etlhubconfirmed.dht_deal_dim where CURRENT_IND='Y'
    group by DEAL_KEY
    having count(*)>1
;

select 'Duplicate Rows based on natural key' as Title,DEAL_ID,count(*) from etlhubconfirmed.dht_deal_dim where CURRENT_IND='Y'
    group by DEAL_ID
    having count(*)>1
;

select 'New rows inserted in this run' as Title,count(*) from etlhubconfirmed.dht_deal_dim where CURRENT_IND='Y'
    and date(extract_dt)='2022-06-28' and rec_status='I'
;

select 'Changed rows inserted in this run' as Title,count(*) from etlhubconfirmed.dht_deal_dim where CURRENT_IND='Y'
    and date(extract_dt)='2022-06-28' and rec_status='U'
;

select 'Changed rows updated in this run' as Title,count(*) from etlhubconfirmed.dht_deal_dim where CURRENT_IND='N'
    and date(REC_END_DT)='2022-06-28' --and rec_status='I'
;

select 'Rows no longer active in source' as Title,count(*) from etlhubconfirmed.dht_deal_dim where CURRENT_IND='Y'
    AND date(REC_END_DT)='2022-06-28' --and rec_status='I'
;
